import React, {useState} from 'react'
import CargarImagen from '../../components/CargarImagenComponent/CargarImagen'

import './styles.css'

const App = () => {

  const fakeDataArray = [
    {
      name: 'daniel',
      edad: 19,
      isAlive: true,
    },
    {
      name: 'Esteban',
      edad: 23,
      isAlive: true,
      profile: 'image URL'
    },
    {
      name: 'Michael Jackson',
      edad: 45,
      isAlive: false,
    }
  ]

  // vivo=10, muerto=5, perdido=5
  // vivo = verde
  // muerto = roja
  // perdido = amarilla

  const [fakeData, setFakeData] = useState(fakeDataArray)

  const addToArray = () => {
    setFakeData([...fakeData, {nombre: 'Unice', edad: 25, isAlive: true}])
    console.log(fakeData)
  }

  return (
    <div>
      <div className="alignItems">
        {
          fakeData.map(user => (
            <CargarImagen addToArray={addToArray} user={user}/>
          ))
        }
      </div>
    </div>
  )
}

export default App
