import React,{useState} from 'react';
import {useHistory} from 'react-router-dom'

const LoginForm = () => {

    const history = useHistory()

    const [emailInput, setEmailInput] = useState('')
    const [passwordInput, setPasswordInput] = useState('')

    const handleEmail = (e) => {
        setEmailInput(e.target.value)
    }

    const handlePassword = (e) => {
        setPasswordInput(e.target.value)
    }

    const handleForm = (e) => {
        e.preventDefault();
        const values = {
            email: emailInput,
            password: passwordInput,
        }
        if(emailInput === '' || passwordInput ===''){ 
            console.log('esta weada esta vacia')
        }else{
            console.log(values)
            history.push('/cards')
        }
        setEmailInput('')
        setPasswordInput('')
    } 

    return (
    <div>
        <div className= "formContainer">
            <form onSubmit={handleForm}>
                <input type="text" name="emailInput" id="emailInput" onChange={handleEmail} value={emailInput}/>

                <label htmlFor="passwordInput">Password: </label>
                <input type="password" name="passwordInput" id="passwordInput" onChange={handlePassword} value={passwordInput}/>

                <button onClick={handleForm}>Login</button>
            </form>
        </div>            
    </div>
    )
}

export default LoginForm
